import React, { useEffect, useState } from "react";
import "../styles/catalog.css";
import lamine from "../productimages/p1.JPG";
import foot from "../productimages/p1.JPG";
import babyHand from "../productimages/p1.JPG";
import petPaw from "../productimages/p1.JPG";

const ProductCatalog = () => {
  const [isVisible, setIsVisible] = useState(false);
  const products = [
    {
      id: 1,
      name: "Hand Impression",
      price: "$99",
      img: lamine,
      description: "A beautiful hand impression in metal.",
    },
    {
      id: 2,
      name: "Foot Impression",
      price: "$89",
      img: foot,
      description: "A unique foot impression in metal.",
    },
    {
      id: 3,
      name: "Baby Handprint",
      price: "$79",
      img: babyHand,
      description: "Capture your baby’s first handprint.",
    },
    {
      id: 4,
      name: "Pet Paw Print",
      price: "$85",
      img: petPaw,
      description: "Cherish your pet with this paw print.",
    },
  ];
  // Handle scroll event
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > window.innerHeight) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  return (
    <div className={`catalog-container ${isVisible ? "slide-up" : ""}`}>
      <h1 className="catalog-heading">Product Catalog</h1>
      <div className="product-card-wrapper">
        {products.map((product) => (
          <div className="product-card" key={product.id}>
            <div className="product-card-inner">
              <div className="product-card-front">
                <img src={product.img} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.price}</p>
              </div>
              <div className="product-card-back">
                <p>{product.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCatalog;
