import React,{useEffect,useState} from "react";
import Navbar from "../components/navbar.tsx";
import ProductCatalog from "../components/catalog";
import "../styles/homepage.css"; // Custom styles here

const HomePage = () => {
  const [slideUp, setSlideUp] = useState(false);

  useEffect(() => {
    const handleScroll = (e) => {
      if (!slideUp && window.scrollY > 0) {
        setSlideUp(true);
        window.scrollTo(0, 0); // Prevents page scroll
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [slideUp]);

  return (
    <div className="homepage">
      
      <Navbar />
      <section className="hero-section">
        <h1>Cherishables</h1>
        <h3 className="hero-heading">Hand and Foot in Metals</h3>
      </section>
      <section className={`catalog-section ${slideUp ? "slide-up" : ""}`}>
        <ProductCatalog />
      </section>
    </div>
  );
};

export default HomePage;
